pub fn unique_paths_with_obstacles(obstacle_grid: Vec&lt;Vec&lt;i32&gt;&gt;) -&gt; i32 {
let m = obstacle_grid.len();
let n = obstacle_grid[0].len();
let mut path = vec![vec![0; n]; m];
path[0][0] = obstacle_grid[0][0] ^ 1;
for i in 0..m {
for j in 0..n {
if obstacle_grid[i][j] == 1 {
continue;
}
if i &gt; 0 {
path[i][j] += path[i - 1][j];
}
if j &gt; 0 {
path[i][j] += path[i][j - 1];
}
}
}
path[m - 1][n - 1]
}
